const isPhone = (phone) => {
  const regEx = /(?:\+?(61))? ?(?:\((?=.*\)))?(0?[2-57-8])\)? ?(\d\d(?:[- ](?=\d{3})|(?!\d\d[- ]?\d[- ]))\d\d[- ]?\d[- ]?\d{3})|(^(?:(?:\+|0{0,2})91(\s*[\ -]\s*)?|[0]?)?[789]\d{9}|(\d[ -]?){10}\d$)/;
  if (phone.match(regEx)) return true;
  else return false;
};

const createCandidate = (rawdata) => {
  let candidate = {
    skill: [],
    email: "placeholder",
    phone: "placeholder",
    location: "placeholder",
    source: "ATS Insert",
    resume_text: "placeholder",
  };
  candidate.resume_text = rawdata;
  // console.log(rawdata);
  var data = formatting(rawdata).replace(/\r\n/g, " ").toLowerCase().split(" ");
  data = data.filter(function (n) {
    return n;
  });

  // console.log(data);

  for (i = 0; i < data.length; i++) {
    if (
      SKILLSET.indexOf(data[i]) != -1 &&
      candidate.skill.indexOf(capitalizeFirstLetter(data[i])) == -1
    ) {
      candidate.skill.push(capitalizeFirstLetter(data[i]));
    }
    if (isEmail(data[i])) candidate.email = data[i];
    else if (isPhone(data[i])) candidate.phone = data[i];
    else if (candidate.location != null && Location.indexOf(data[i]) != -1) {
      candidate.location = capitalizeFirstLetter(data[i]);
    }
  }
  if (candidate.email === "placeholder") candidate.email = `placeholder_${data[0]}`;
  if (candidate.phone === "placeholder") candidate.phone = `placeholder_${data[1]}`;
  return candidate;
};

const pdfParser = (pdffile) => {
  // console.log(pdffile);
  return new Promise((resolve, reject) => {
    // Set up the pdf parser
    let pdfParser = new PDFParser(this, 1);
    // Load the pdf document
    pdfParser.loadPDF(pdffile);
    pdfParser.on("pdfParser_dataError", (errData) =>
      reject(new Error(errData.parserError))
    );
    pdfParser.on("pdfParser_dataReady", () => {
      let rawdata = pdfParser.getRawTextContent().split("\r\n");
      rawdata = rawdata.map((data) => data.replace(/ /g, '').replace(/[•\t]/g, ''));
      rawdata = rawdata.join(" ");
      if (rawdata != null) {
        var candidate = createCandidate(rawdata);
        resolve(candidate);
      }
    });
  }).catch((error) => {
    console.log(error);
  });
};

const wordParser = (wordFile) => {
  return new Promise((resolve, reject) => {
    // const extractor = new wordExtractor();
    // const extracted = extractor.extract('./src/getResume/Rakesh Auplish BI Specialist Resume.docx');
    // extracted.then(function (rawData) {
    //   console.log(rawData.getBody());

    //   rawData = rawData.getBody().split("\n");
    //   rawData = rawData.map((data) => data.replace(/ /g, '').replace(/[•\t]/g, ''));
    //   rawData = rawData.join(" ");
    //   if (rawData != null) {
    //     var candidate = createCandidate(rawData);
    //     resolve(candidate);
    //   }
    // });
    textract.fromFileWithPath(wordFile, function (error, rawData) {
      if (!error) {
        rawData = rawData.split("\n");
        rawData = rawData.map((data) => data.replace(/[•\t]/g, ''));
        rawData = rawData.join(" ");
        if (rawData != null) {
          var candidate = createCandidate(rawData);
          resolve(candidate);
        }
      }
    });
  }).catch((error) => {
    console.log(error);
  });
};

module.exports = { pdfParser, wordParser };
